import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import styled from "styled-components";
import { useState } from "react";

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeList = ({ theme }) => {
  return (
    <ThemeListWrapper border={theme.border.colors.primary}>
      <FontAwesomeIcon icon={faPlusCircle} color={theme.icon.colors.other.c} />
      <SimpleText {...GridElementHeaderProps(theme)}>Default</SimpleText>
      <SimpleText {...GridElementHeaderProps(theme)}>Theme 1</SimpleText>
    </ThemeListWrapper>
  );
};

const AddOrEditTheme = (theme) => {
  const [themeOptions, setThemeOptions] = useState(theme);

  return         <form onSubmit={this.handleSubmit}>
  <fieldset>
    <legend>{this.props.formType}</legend>
    { this.renderErrors() }
    <Fields update={this.update} state={this.state} />
    <input className='submit' type="submit" value={this.props.formType} />
  </fieldset>
</form>
}

export const ManageThemes = ({ theme, setTheme }) => {


  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        >
          <ThemeList theme={theme} />
        </GridElement>
      </Grid>
    </React.Fragment>
  );
};
