export const getThemes = async () => {
    const res = await fetch("http://localhost:7070/themes");
    const themes = await res.json();
    console.log(themes);
    return themes;
}

export const updateTheme = async (theme) => {
    const res = await fetch(`http://localhost:7070/theme/${theme.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(theme),
      });
    const updatedTheme = await res.json();
    console.log(updatedTheme);
    return updatedTheme;
}

export const createTheme = async (theme) => {
  const res = await fetch(`http://localhost:7070/theme`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(theme),
    });
  const createdTheme = await res.json();
  console.log(createdTheme);
  return createdTheme;
}
