import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle, faEdit } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled, { useTheme } from "styled-components";
import { useState, useRef } from "react";
import { CompactPicker } from "react-color";
import { RelativePop } from "../CoreUI/Popover/RelativePop";
import { useThemeOptions } from "../../hooks/useThemeOptions";
import { DefaultTheme } from "../../themes/DefaultTheme";

const ThemeOptionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const ThemeOption = (theme, onSelectTheme, onEditTheme, editable = true) => {
  return (
    <ThemeOptionWrapper onClick={onSelectTheme}>
      <SimpleText {...GridElementHeaderProps(currentTheme)}>
        {theme.name}
      </SimpleText>
      {editable && onEditTheme && (
        <FontAwesomeIcon onClick={onEditTheme} icon={faEdit} />
      )}
    </ThemeOptionWrapper>
  );
};

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeListView = ({ currentTheme, themeOptions, onAddOrUpdateTheme }) => {
  const editTheme = (theme) => {
    return () => {
      onAddOrUpdateTheme(theme);
    };
  };

  const selectTheme = (theme) => {
    return () => {
      console.log(theme);
    };
  };

  const textStyle = { ...GridElementHeaderProps(currentTheme) };

  return (
    <ThemeListWrapper border={currentTheme.border.colors.primary}>
      <FontAwesomeIcon
        icon={faPlusCircle}
        color={currentTheme.icon.colors.other.c}
        onClick={onAddOrUpdateTheme}
      />
      {themeOptions.map((themeOption) => {
        return (
          <ThemeOption
            theme={themeOption}
            textStyle={textStyle}
            onSelectTheme={selectTheme(theme)}
            onEditTheme={editTheme(themeOption)}
          />
        );
      })}
    </ThemeListWrapper>
  );
};

const ThemeFieldWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 10px;
`;

const ColorSquare = styled.div`
  background-color: ${(props) => props.color};
  height: 15px;
  width: 15px;
  &:hover {
    cursor: pointer;
  }
`;

const ThemeField = ({
  currentTheme,
  displayName,
  value,
  fieldKey,
  updateTheme,
}) => {
  const fieldRef = useRef(null);
  const getFieldRef = () => {
    return fieldRef;
  };

  const [pickerOpen, setPickerOpen] = useState(false);

  const onChangeComplete = (newColor) => {
    updateTheme(fieldKey, newColor.hex);
    setPickerOpen(false);
  };

  const onTogglePicker = () => setPickerOpen(!pickerOpen);

  return (
    <ThemeFieldWrapper ref={optionRef}>
      <ColorSquare color={value} onClick={onTogglePicker} />
      {pickerOpen && (
        <RelativePop
          height="89px"
          width="245px"
          position={{
            horizontal: 7.5,
            vertical: 0,
          }}
          margin="20px 0 0 0"
          arrowPosition={3}
          backgroundColor={currentTheme.surface.colors.primary}
          borderColor="black"
          onClickAway={onTogglePicker}
          getParentRef={getFieldRef}
        >
          <CompactPicker color={value} onChangeComplete={onChangeComplete} />
        </RelativePop>
      )}
      <SimpleText
        color={currentTheme.text.colors.primary}
        style={currentTheme.text.styles.primary}
        size={currentTheme.text.sizes.large}
      >
        {displayName}
      </SimpleText>
    </ThemeFieldWrapper>
  );
};

const ThemeFormWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 20px;
`;

const ThemeFormView = ({ currentTheme, initialValues, formType }) => {
  const [themeValues, setThemeValues] = useState(initialValues);

  const updateTheme = (fieldKey, newVal) => {
    setThemeValues({
      ...themeValues,
      [fieldKey]: { ...themeValues[optionKey], value: newVal },
    });
  };

  return (
    <ThemeFormWrapper>
      <ThemeField
        currentTheme={currentTheme}
        displayName={themeValues.color.displayName}
        value={themeValues.color.value}
        fieldKey={"color"}
        updateTheme={updateTheme}
      ></ThemeField>
    </ThemeFormWrapper>
  );
};

export const ManageThemes = ({ theme: currentTheme, setTheme }) => {
  const [selectedThemeForm, setSelectedThemeForm] = useState(null);
  const { themeOptions } = useThemeOptions();

  const onSelectThemeForm = (formFields) => {
    setSelectedThemeForm(formFields);
  };

  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        >
          {selectedThemeForm ? (
            <ThemeFormView
              currentTheme={theme}
              initialValues={{
                color: { value: "red", displayName: "Color" },
              }}
            />
          ) : (
            <ThemeListView
              themeOptions={themeOptions}
              currentTheme={currentTheme}
              onAddOrUpdateTheme={onSelectThemeForm}
            />
          )}
        </GridElement>
      </Grid>
    </React.Fragment>
  );
};
