import { useTabContext } from "../../../contexts/tabContext";
import styled from "styled-components";
import { TabMenu } from "./TabMenu";
import { Tabs } from "../../Tabs/index";
import { ThemeContext } from "styled-components";
import { useContext } from "react";

const TabWrapper = styled.div`
  display: flex;
  flex-direction: row;
  ${(props) => `background-color: ${props.backgroundColor}`}
`;

const TabBodyWrapper = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 30px 0;
  > * {
    margin-bottom: 30px;
  }
`;

export const Tab = () => {
  const { tabName } = useTabContext();
  const theme = useContext(ThemeContext);

  // not sure why this is'nt working
  // when trying this, home was exported as default
  // const [TabBody, setTabBody] = useState(<div></div>);

  // useEffect(() => {
  //   async function loadTab() {
  //     const module = await import(`../../Tabs/${tabName}`);
  //     setTabBody(module.default);
  //   }

  //   if (tabName) {
  //     loadTab();
  //   }
  // }, [tabName]);

  // here is the current workaround for the above
  // not ideal because we dont want to import every single tab into this file :(
  const { body: TabBody } = Tabs[tabName] || {};

  return (
    <TabWrapper backgroundColor={theme.background.colors.primary}>
      <TabMenu tabData={Tabs} theme={theme} />
      <TabBodyWrapper>
        <div>{TabBody && TabBody({ theme })}</div>
      </TabBodyWrapper>
    </TabWrapper>
  );
};
