import styled from "styled-components";
import Link from "next/link";

const LandingContainer = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
`;

export const LogoWrapper = styled.div`
  background: transparent linear-gradient(218deg, #242348 0%, #5a55aa 100%) 0%
    0% no-repeat padding-box;
  background-size: fill;
  width: 50%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  @media (max-width: 812px) {
    display: none;
  }
`;

export const LogoImg = styled.img`
  width: 300px;
  height: 258px;
`;

const PortalHeading = styled.div`
  text-align: center;
  flex: auto;
  margin-top: 30vh;
  overflow: hidden;
  @media (max-width: 812px) {
    margin-top: 15vh;
  }
`;

const StreamerPortal = styled.h1`
  text-align: center;
  letter-spacing: 7px;
  color: #6441a5;
  opacity: 1;
  font-family: "Source Sans Pro", sans-serif;
  font-weight: bold;
  font-size: 35px;
`;

const WelcomeMessage = styled.h3`
  font: Regular 18px/13px;
  letter-spacing: 0px;
  color: #4d4f5c;
  opacity: 0.5;
  font-family: "Source Sans Pro", sans-serif;
  font-weight: lighter;
`;

const Button = styled.div`
  margin-top: 16.5vh;
  @media (max-width: 812px) {
    display: flex;
    flex-direction: column;
  }
`;

const LoginBtn = styled.button`
  margin-right: 30px;
  border: 1px solid #6441a5;
  border-radius: 4px;
  opacity: 1;
  background: #6441a5 0% 0% no-repeat padding-box;
  width: 185px;
  height: 50px;
  color: #ffffff;
  font-size: 18px;
  font-family: "Source Sans Pro", sans-serif;
  cursor: pointer;
  &:hover {
    background-color: #563691;
  }
  @media (max-width: 812px) {
    margin-bottom: 30px;
    align-self: center;
    margin-left: 30px;
  }
`;

const RegisterBtn = styled.button`
  border: 1px solid #6441a5;
  border-radius: 4px;
  opacity: 1;
  width: 195px;
  height: 50px;
  text-align: center;
  font-family: "Source Sans Pro", sans-serif;
  color: #4d4f5c;
  opacity: 1;
  font-size: 18px;
  cursor: pointer;
  &:hover {
    border: 1px solid #563691;
  }
  @media (max-width: 812px) {
    align-self: center;
  }
`;

const Terms = styled.div`
  text-align: center;
  color: #6441a5;
  opacity: 1;
  font-family: "Source Sans Pro", sans-serif;
  font-size: 11px;
  margin-top: 27vh;
  cursor: pointer;
  text-decoration: none;
  @media (max-width: 812px) {
    margin-top: 130px;
  }
`;
const TermsLink = styled.a`
  margin-right: 5px;
`;

export const A = styled.a`
  text-decoration: none;
  cursor: pointer;
`;

const Landing = () => {
  return (
    <LandingContainer>
      <LogoWrapper>
        <LogoImg src="/images/logo.png" alt="logo" />
      </LogoWrapper>
      <PortalHeading>
        <StreamerPortal>STREAMER PORTAL</StreamerPortal>
        <WelcomeMessage>
          Welcome back! Please login to your account.
        </WelcomeMessage>
        <Button>
          <Link
            href="//gatekeeper.wearehazard.com/v1/session/twitch"
            passRef={true}
          >
            <LoginBtn>Login with Twitch</LoginBtn>
          </Link>
          <Link
            href="//gatekeeper.wearehazard.com/v1/session/twitch"
            passRef={true}
          >
            <RegisterBtn>Register with Twitch</RegisterBtn>
          </Link>
        </Button>
        <div>
          <Terms>
            <Link href="/terms-and-conditions">
              <TermsLink>Term of use.</TermsLink>
            </Link>
            <Link href="/privacy-policy">
              <A>Privacy Policy</A>
            </Link>
          </Terms>
        </div>
      </PortalHeading>
    </LandingContainer>
  );
};

export default Landing;
