import { getThemes, updateTheme, createTheme, deleteTheme } from "../api/theme_api"
import { useEffect, useState } from "react";
import defaultThemeValues from "../themes/defaultThemeValues";

const defaultTheme = { ...defaultThemeValues, name: "Default"};


export const useThemeOptions = () => {
    const [themeOptions, setThemeOptions] = useState([defaultTheme]);
    const [error, setError] = useState(false);

    useEffect(() => {
        const callGetThemes = async () => {
            const { error, response } = await getThemes();
            if (error) {
                setError(true);
            } else if (response) {
                setThemeOptions([defaultTheme].concat(options));
            }  
        }

        callGetThemes();
    }, []);

    const onUpdateTheme = async (theme) => {
        const updatedTheme = await updateTheme(theme);
        const updatedThemeOptions = themeOptions.map(theme => theme.id !== updatedTheme.id ? theme : updatedTheme);
        setThemeOptions(updatedThemeOptions);
    }

    const onCreateTheme = async (theme) => {
        const newTheme = await createTheme(theme);
        const updatedThemeOptions = themeOptions.concat([newTheme]);
        setThemeOptions(updatedThemeOptions);
    }

    const onDeleteTheme = async (themeId) => {
        await deleteTheme(themeId);
        const updatedThemeOptions = themeOptions.filter(theme => theme.id !== themeId);
        setThemeOptions(updatedThemeOptions);
    }

    return { themeOptions, onUpdateTheme, onCreateTheme, onDeleteTheme }
}
