const x = ["background", "colors", "primary"]


const addField = (fieldKeyArray, value, theme) => {
    const currentKey = fieldKeyArray[0];

    if (fieldKeyArray.length === 1) {
        theme[currentKey] = value;
    } else {
        const nextKey = fieldKeyArray[1];

        if (!theme[currentKey]) {
            theme[currentKey] = {};
        }

        theme[currentKey][nextKey] = addField(fieldKeyArray.slice(1), value, theme[currentKey]);
    }

    return theme;
}

export default (themeFields) => {
    const theme = Object.keys(themeFields).reduce((result, fieldKey) => {
        result = addField(fieldKey.split("_"), themeFields[fieldKey], result);

        return result;
    }, {});
    console.log("BUILT THEME");
    console.log(theme);
    return theme;
}
