export const getStatsById = async (statsId) => {
    const res = await fetch(`http://localhost:7070/stats/${statsId}`);
    const resVal = await res.json();

    if (res.status !== 200) {
        if (resVal.message) console.log(resVal.message);
        return { error: true, response: undefined };
    }

    return { error: false, response: resVal };
}
