const addField = (fieldKeyArray, value, theme) => {
    const currentKey = fieldKeyArray[0];

    if (fieldKeyArray.length === 1) {
        return { [currentKey]: value };
    } else {
        if (!theme[currentKey]) {
            theme[currentKey] = {};
        }
        theme[currentKey] = { ...theme[currentKey], ...addField(fieldKeyArray.slice(1), value, theme[currentKey]) };
    }

    return theme;
}

export default (themeFields) => {
    const x =  Object.keys(themeFields).reduce((result, fieldKey) => {
        result = addField(fieldKey.split("_"), themeFields[fieldKey], result);

        return result;
    }, {});
    console.log(x);
}
