import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import { Chart } from "../CoreUI/Chart/Chart";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { GridElementHeaderProps } from "../../utils/styleUtils/commonTextProps";
import { MultiText } from "../CoreUI/Text/MultiText";
import styled from "styled-components";
import { centerMixin } from "../../utils/styleUtils/generalCSSUtils";
import { TabSectionHeaderProps } from "../../utils/styleUtils/commonTextProps";

const StreamPerformanceChart = () => {
  return (
    <Chart
      chartConfig={{
        chart: {
          type: "column",
        },
        title: {
          text: null,
        },
        legend: {
          verticalAlign: "top",
        },
        xAxis: {
          categories: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
          crosshair: true,
        },
        yAxis: {
          min: 0,
          title: {
            text: null,
          },
        },
        tooltip: {
          headerFormat:
            '<span style="font-size:10px">{point.key}</span><table>',
          pointFormat:
            '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
            '<td style="padding:0"><b>{point.y:.1f} mm</b></td></tr>',
          footerFormat: "</table>",
          shared: true,
          useHTML: true,
        },
        plotOptions: {
          column: {
            pointPadding: 0.2,
            borderWidth: 0,
          },
        },
        series: [
          {
            name: "Average Viewers",
            color: "#A4A1FB",
            data: [49.9, 71.5, 106.4, 129.2, 144.0, 176.0, 135.6],
          },
          {
            name: "Unique Viewers",
            color: "#56D9FE",
            data: [83, 78, 98, 93, 106, 84, 105],
          },
        ],
        credits: {
          enabled: false,
        },
      }}
    />
  );
};

const ViewSourceDistributionChart = () => {
  return (
    <Chart
      chartConfig={{
        chart: {
          type: "pie",
        },
        title: {
          text: null,
        },
        legend: {
          align: "right",
          layout: "vertical",
          verticalAlign: "middle",
          itemMarginBottom: 12,
        },
        plotOptions: {
          pie: {
            shadow: false,
            center: ["50%", "50%"],
            showInLegend: true,
          },
        },
        tooltip: {
          valueSuffix: "%",
        },
        series: [
          {
            name: "Distribution",
            data: [
              { name: "Followers", y: 20, color: "blue" },
              { name: "Searches", y: 15, color: "red" },
              { name: "Other", y: 30, color: "green" },
              { name: "Channels", y: 35, color: "purple" },
            ],
            size: "80%",
            innerSize: "60%",
            dataLabels: {
              enabled: false,
            },
            id: "distribution",
          },
        ],
        responsive: {
          rules: [
            {
              condition: {},
              chartOptions: {
                series: [
                  {},
                  {
                    id: "versions",
                    dataLabels: {
                      enabled: false,
                    },
                  },
                ],
              },
            },
          ],
        },
        credits: {
          enabled: false,
        },
      }}
    />
  );
};

const SingleStatMetricWrapper = styled.div`
  ${centerMixin}
  flex-direction: column;
  height: 100%;
`;

const SingleStatMetric = ({
  textTheme: { styles, sizes, colors },
  percentChangeColor,
  textData,
}) => {
  return (
    <SingleStatMetricWrapper>
      <MultiText
        textConfigs={[
          {
            content: textData[0],
            style: styles.primary,
            size: sizes.medium,
            color: colors.secondary,
            moreStyles: "opacity: 50%",
          },
          {
            content: textData[1],
            style: styles.bold,
            size: sizes.xxlarge,
            color: colors.primary,
            weight: "bold",
          },
          {
            content: textData[2],
            style: styles.bold,
            size: sizes.xsmall,
            color: percentChangeColor,
            weight: "bold",
          },
        ]}
      />
    </SingleStatMetricWrapper>
  );
};

export const Dashboard = ({ theme }) => {
  return [
    <SimpleText {...TabSectionHeaderProps(theme)}>Stream Overview</SimpleText>,
    <Grid numCols={12} numRows={4} rowUnit="135px" colUnit="56px" key={1}>
      <GridElement
        position={{
          startCol: 1,
          endCol: 7,
          startRow: 2,
          endRow: 4,
        }}
      >
        <SimpleText {...GridElementHeaderProps(theme)}>
          View Source Distribution
        </SimpleText>
        <ViewSourceDistributionChart />
      </GridElement>
      <GridElement
        position={{
          startCol: 7,
          endCol: 13,
          startRow: 2,
          endRow: 4,
        }}
      >
        <SimpleText {...GridElementHeaderProps(theme)}>
          Stream Performance
        </SimpleText>
        <StreamPerformanceChart />
      </GridElement>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SingleStatMetric
          textData={["Total Views", "246K", "13%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SingleStatMetric
          textData={["Engagements", "2453", "12%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SingleStatMetric
          textData={["Follower Reach", "25.50%", "12%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 4,
          endRow: 5,
        }}
      >
        <SingleStatMetric
          textData={["Total Views", "246K", "13%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 4,
          endRow: 5,
        }}
      >
        <SingleStatMetric
          textData={["Engagements", "2453", "12%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 4,
          endRow: 5,
        }}
      >
        <SingleStatMetric
          textData={["Follower Reach", "25.50%", "12%"]}
          textTheme={theme.text}
          percentChangeColor={theme.text.colors.other.e}
        />
      </GridElement>
    </Grid>,
  ];
};
