import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import { TabSectionHeaderProps } from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import styled from "styled-components";

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  border-top: ${(props) => `1px solid ${props.border}`};
  ${(props) => `background-color: ${props.backgroundColor}`};
`;

const ThemeList = () => {
  return (
    <ThemeListWrapper
      backgroundColor={theme.background.colors.primary}
      border={theme.border.colors.primary}
    >         <SimpleText {...GridElementHeaderProps(theme)}>
    View Source Distribution
  </SimpleText>
  </ThemeListWrapper>
  );
};

export const ManageThemes = ({ theme, setTheme }) => {
  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        ></GridElement>
      </Grid>
    </React.Fragment>
  );
};
