import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { useState } from "react";
import { SketchPicker } from "react-color";

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeList = ({ theme }) => {
  return (
    <ThemeListWrapper border={theme.border.colors.primary}>
      <FontAwesomeIcon icon={faPlusCircle} color={theme.icon.colors.other.c} />
      <SimpleText {...GridElementHeaderProps(theme)}>Default</SimpleText>
      <SimpleText {...GridElementHeaderProps(theme)}>Theme 1</SimpleText>
    </ThemeListWrapper>
  );
};

const ThemeOptionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin-bottom: 10px;
`;

const ThemeOption = ({ displayName, value, optionKey, updateTheme }) => {
  const onChangeComplete = (newColor) => {
    updateTheme(optionKey, newColor);
  };

  return (
    <ThemeOptionWrapper>
      <SimpleText>{displayName}</SimpleText>
      <SketchPicker color={value} onChangeComplete={onChangeComplete} />
    </ThemeOptionWrapper>
  );
};

const ThemeFormWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 20px;
`;

const ThemeForm = ({ currentTheme, initialThemeOptions, formType }) => {
  const [themeOptions, setThemeOptions] = useState(initialThemeOptions);

  const updateTheme = (optionKey, newVal) => {
    setThemeOptions({
      ...themeOptions,
      [optionKey]: newVal,
    });
  };

  return (
    <ThemeFormWrapper>
      <ThemeOption
        name={editingThemeOptions.color.displayName}
        value={editingThemeOptions.color.value}
        optionKey={"color"}
        updateTheme={updateTheme}
      ></ThemeOption>
    </ThemeFormWrapper>
  );
};

export const ManageThemes = ({ theme, setTheme }) => {
  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        >
          <ThemeList
            theme={theme}
          />
          <ThemeForm editingThemeOptions={{ color: { value: "red", displayName: "Color" } }}/>

        </GridElement>
      </Grid>
    </React.Fragment>
  );
};
