import { useRef, useEffect } from "react";
import PropTypes from 'prop-types';
import styled from 'styled-components';

const BackgroundDiv = styled.div`
  position: relative;
  z-index: 11;
`;

/**
 * Hook that alerts clicks outside of the passed ref
 */
function useOutsideAlerter(ref, handler, getParentRef) {

    useEffect(() => {
        /**
         * Alert if clicked on outside of element
         */
        function handleClickOutside(event) {
            event.preventDefault();
            if (ref.current && !ref.current.contains(event.target)) {
                if (getParentRef) {
                    let elem = getParentRef();
                    if (!elem.current.contains(event.target)) {
                        handler();
                    }
                } else {
                    handler();
                }
            }
        }

        // Bind the event listener
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            // Unbind the event listener on clean up
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [ref]);
}

const CatchOutside = ({ children, handler, getParentRef }) => {
    const wrapperRef = useRef(null);
    useOutsideAlerter(wrapperRef, handler, getParentRef);

    return <BackgroundDiv ref={wrapperRef}>{children}</BackgroundDiv>;
}

CatchOutside.propTypes = {
    children: PropTypes.any,
    handler: PropTypes.func,
    getParentRef: PropTypes.func
}

export default CatchOutside;
