import { GridElementHeaderProps } from "../../../utils/styleUtils/commonTextProps";
import {
  faPlusCircle,
  faEdit,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { GridElement } from "../../CoreUI/Grid/GridElement";
import { Grid } from "../../CoreUI/Grid/Grid";
import defaultThemeValues from "../../../themes/defaultThemeValues";
import { SimpleText } from "../../CoreUI/Text/SimpleText";

const ThemeOptionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  > * {
    padding: 10px 10px 0 0;
  }
`;

const ThemeOption = ({
  theme,
  textStyle,
  onSelectTheme,
  onEditTheme,
  onDeleteTheme,
  editable = true,
}) => {
  const handleEditOrDelete = (method) => {
    return (e) => {
      e.stopPropagation();
      method();
    };
  };

  return (
    <ThemeOptionWrapper onClick={onSelectTheme}>
      <SimpleText {...textStyle}>{theme.name}</SimpleText>
      {editable && onEditTheme && (
        <FontAwesomeIcon
          onClick={handleEditOrDelete(onEditTheme)}
          icon={faEdit}
        />
      )}
      {editable && onDeleteTheme && (
        <FontAwesomeIcon
          onClick={handleEditOrDelete(onDeleteTheme)}
          icon={faTrash}
        />
      )}
    </ThemeOptionWrapper>
  );
};

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

export const ThemeListView = ({
  currentTheme,
  themeOptions,
  onAddOrUpdateTheme,
  onDeleteTheme,
  onSelectTheme,
}) => {
  if (!themeOptions) {
    return <div>Loading...</div>;
  }

  const createTheme = () => {
    onAddOrUpdateTheme(defaultThemeValues);
  };

  const editTheme = (theme) => {
    return () => {
      onAddOrUpdateTheme(theme);
    };
  };

  const selectTheme = (theme) => {
    return () => {
      onSelectTheme(theme);
    };
  };

  const deleteTheme = (themeId) => {
    return () => {
      onDeleteTheme(themeId);
    };
  };

  const textStyle = {
    color: currentTheme.text.colors.primary,
    style: currentTheme.text.styles.primary,
    size: currentTheme.text.sizes.large,
  };

  return (
    <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
      <GridElement
        position={{
          startCol: 1,
          endCol: 17,
          startRow: 1,
          endRow: 10,
        }}
      >
        <ThemeListWrapper border={currentTheme.border.colors.primary}>
          <FontAwesomeIcon
            icon={faPlusCircle}
            color={currentTheme.icon.colors.other.c}
            onClick={createTheme}
          />
          {themeOptions.map((themeOption) => {
            return (
              <ThemeOption
                theme={themeOption}
                textStyle={textStyle}
                onSelectTheme={selectTheme(themeOption)}
                onEditTheme={editTheme(themeOption)}
                onDeleteTheme={deleteTheme(themeOption.id)}
              />
            );
          })}
        </ThemeListWrapper>
      </GridElement>
    </Grid>
  );
};
