import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { useState } from "react";
import { SketchPicker } from 'react-color';

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeList = ({ theme }) => {
  return (
    <ThemeListWrapper border={theme.border.colors.primary}>
      <FontAwesomeIcon icon={faPlusCircle} color={theme.icon.colors.other.c} />
      <SimpleText {...GridElementHeaderProps(theme)}>Default</SimpleText>
      <SimpleText {...GridElementHeaderProps(theme)}>Theme 1</SimpleText>
    </ThemeListWrapper>
  );
};

const Fields = ({ update, themeOptions }) => {
  <FieldsWrapper>
    <label htmlFor="name">
      <span>
        Order Name <span className="required-field">*</span>
      </span>
      <input
        type="text"
        value={state.name}
        name="name"
        onChange={update("name")}
      />
    </label>

    <label htmlFor="data_package_type">
      <span>
        Data Package Type <span className="required-field">*</span>
      </span>
      <select
        value={state.data_package_type}
        name="data_package_type"
        onChange={update("data_package_type")}
      >
        <option value=""></option>
        <option value="Device Location">Device Location</option>
        <option value="Device Behavior">Device Behavior</option>
        <option value="ID Mapping">ID Mapping</option>
      </select>
    </label>

    <label htmlFor="max_bid_price">
      <span>
        Max Bid Price <span className="required-field">*</span>
      </span>
      <input
        type="number"
        min="0"
        max="1000000000000000"
        step="0.01"
        value={state.max_bid_price}
        name="max_bid_price"
        onChange={update("max_bid_price")}
      />
    </label>
  </FieldsWrapper>;
};

const ThemeFormWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 20px;
`;

const ThemeOption = ({ displayName, value, optionKey, updateTheme }) => {
  const onChangeComplete = (newColor) => {
    updateTheme(optionKey, newColor);
  }

  return <ThemeOptionWrapper>
    <SimpleText>{displayName}</SimpleText>
    <SketchPicker color={value} onChangeComplete={onChangeComplete} />
  </ThemeOptionWrapper>
}

const ThemeForm = ({ theme, formType }) => {
  const [themeOptions, setThemeOptions] = useState(theme);

  const updateTheme = (optionKey, newVal) => {
    setThemeOptions({
      ...themeOptions,
      [optionKey]: newVal
    });
  }

  return (
    <ThemeFormWrapper>
      <ThemeOption name={theme.color.displayName} value={theme.color.value} optionKey={"color"} updateTheme={updateTheme}></ThemeOption>
    </ThemeFormWrapper>
  );
};

export const ManageThemes = ({ theme, setTheme }) => {
  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        >
          <ThemeList theme={{ color: { value: "red", displayName: "Color" } }} />
        </GridElement>
      </Grid>
    </React.Fragment>
  );
};
