import { getThemes } from "../api/theme_api"
import { useEffect, useState } from "react";


export const useThemeOptions = () => {
    const [themeOptions, setThemeOptions] = useState();

    useEffect(async () => {
        const options = await getThemes();

        setThemeOptions(options);
    }, []);

    // const getTheme

    return { themeOptions }
}
