import styled from "styled-components";
import { SimpleText } from "../Text/SimpleText";
import { DecoratedText } from "../Text/DecoratedText";
import { WithDecoration } from "../Auxiliary/WithDecoration";
import { ThemeContext } from "styled-components";
import { useContext } from "react";
import { centerMixin } from "../../../utils/styleUtils/generalCSSUtils";
import { useState } from "react";

const StyledAppBar = styled.div`
  height: 70px;
  width: 100%;
  display: flex;
`;

const AppBarLeft = styled.div`
  min-width: 210px;
  background-color: ${(props) => props.backgroundColor};
  ${centerMixin}
`;

const AppBarRight = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
  margin-right: 20px;
`;

export const AppBar = () => {
  const theme = useContext(ThemeContext);
  const { tab, text } = theme;

  return (
    <StyledAppBar>
      <AppBarLeft backgroundColor={tab.colors.primary}>
        <SimpleText
          color={text.colors.menuItem}
          style={text.styles.bold}
          size={text.sizes.medium}
          weight="bold"
        >
          STREAMER PORTAL
        </SimpleText>
      </AppBarLeft>
      <AppBarRight>
        <UserMenu />
      </AppBarRight>
    </StyledAppBar>
  );
};

const StyledUserMenu = styled.div`
  display: flex;
  align-items: center;
`;

const Arrow = styled.div`
  ${(props) =>
    `transform: rotate(${props.direction === "up" ? "-135" : "45"}deg);`}
  border: solid black;
  border-width: 0 3px 3px 0;
  display: inline-block;
  padding: 3px;
`;

const UserMenu = () => {
  const { text } = useContext(ThemeContext);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <StyledUserMenu>
      <WithDecoration>
        <DecoratedText
          decoration={{ end: <Arrow direction={dropdownOpen} />, endWidth: 12 }}
          color={text.colors.primary}
          style={text.styles.primary}
          size={text.sizes.small}
        >
          User
        </DecoratedText>
      </WithDecoration>
    </StyledUserMenu>
  );
};
