import { Grid } from "../CoreUI/Grid/Grid";
import { GridElement } from "../CoreUI/Grid/GridElement";
import { GridElementHeaderProps } from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { DecoratedText } from "../CoreUI/Text/DecoratedText";

const ThemeFieldWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 10px;
`;

const ColorSquare = styled.div`
  background-color: ${(props) => props.color};
  height: 15px;
  width: 15px;
  &:hover {
    cursor: pointer;
  }
`;

const ThemeField = ({
  textConfig,
  popColor,
  displayName,
  value,
  fieldKey,
  updateTheme,
}) => {
  const fieldRef = useRef(null);
  const getFieldRef = () => {
    return fieldRef;
  };

  const [pickerOpen, setPickerOpen] = useState(false);

  const onChangeComplete = (newColor) => {
    updateTheme(fieldKey, newColor.hex);
    setPickerOpen(false);
  };

  const onTogglePicker = () => setPickerOpen(!pickerOpen);

  return (
    <ThemeFieldWrapper ref={fieldRef}>
      {pickerOpen && (
        <RelativePop
          height="89px"
          width="245px"
          position={{
            horizontal: 7.5,
            vertical: 0,
          }}
          margin="20px 0 0 0"
          arrowPosition={3}
          backgroundColor={currentTheme.surface.colors.primary}
          borderColor="black"
          onClickAway={onTogglePicker}
          getParentRef={getFieldRef}
        >
          <CompactPicker color={value} onChangeComplete={onChangeComplete} />
        </RelativePop>
      )}
      <DecoratedText
        {...textConfig}
        decoration={<ColorSquare color={value} onClick={onTogglePicker} />}
      >
        {displayName}
      </DecoratedText>
    </ThemeFieldWrapper>
  );
};

export const ThemeForm = ({ currentTheme, initialValues }) => {
  const [themeValues, setThemeValues] = useState(initialValues);

  const updateTheme = (fieldKey, newVal) => {
    setThemeValues({
      ...themeValues,
      [fieldKey]: newVal,
    });
  };

  const {
    text: {
      styles: { primary: primaryStyle },
      colors: { primary: primaryColor },
      sizes: { small },
    },
    surface: {
      colors: { primary: popColor },
    },
  } = currentTheme;

  const fieldTextConfig = {
    style: primaryStyle,
    size: small,
    color: primaryColor,
  };

  const fields = Object.keys(initialValues).reduce((result, fieldKey) => {
    const fieldVal = initialValues[fieldKey];

    const fieldKeyParts = fieldKey.split("_");
    const fieldGroup = fieldKeyParts[0];
    const field = fieldKeyParts[fieldKeyParts.length - 1];

    if (!result[fieldGroup]) {
      result[fieldGroup] = {};
    }

    result[fieldGroup][field] = {
      fieldKey,
      displayName: field[0].toUpperCase() + field.slice(1),
      value: fieldVal,
    };

    return result;
  }, {});

  const getFieldsByGroup = (groupKey) => {
    return Object.entires(groupKey).map((field) => {
      return (
        <ThemeField
          textConfig={fieldTextConfig}
          popColor={popColor}
          displayName={field.displayName}
          value={field.value}
          fieldKey={field.fieldKey}
          updateTheme={updateTheme}
        />
      );
    });
  };

  return (
    <Grid numCols={12} numRows={3} rowUnit="135px" colUnit="56px" key={1}>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>
          Background
        </SimpleText>
        {getFieldsByGroup(fields.background)}
      </GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>
          Border
        </SimpleText>
        {getFieldsByGroup(fields.currentTheme)}
      </GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>
          Surface
        </SimpleText>
        {getFieldsByGroup(fields.surface)}
      </GridElement>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 2,
          endRow: 4,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>Text</SimpleText>
        {getFieldsByGroup(fields.text)}
      </GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 2,
          endRow: 4,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>Icon</SimpleText>
        {getFieldsByGroup(fields.icon)}
      </GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 2,
          endRow: 3,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>Tab</SimpleText>
        {getFieldsByGroup(fields.tab)}
      </GridElement>
    </Grid>
  );
};
