import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle, faEdit } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { useState, useRef } from "react";
import { CompactPicker } from "react-color";
import { RelativePop } from "../CoreUI/Popover/RelativePop";
import { useThemeOptions } from "../../hooks/useThemeOptions";
import { ThemeForm } from "./theme_form";
import buildTheme from "../../themes/buildTheme";

const ThemeOptionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const ThemeOption = ({
  theme,
  textStyle,
  onSelectTheme,
  onEditTheme,
  editable = true,
}) => {
  return (
    <ThemeOptionWrapper onClick={onSelectTheme}>
      <SimpleText {...textStyle}>{theme.name}</SimpleText>
      {editable && onEditTheme && (
        <FontAwesomeIcon onClick={onEditTheme} icon={faEdit} />
      )}
    </ThemeOptionWrapper>
  );
};

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeListView = ({ currentTheme, themeOptions, onAddOrUpdateTheme }) => {
  if (!themeOptions) {
    return <div>Loading...</div>;
  }

  const editTheme = (theme) => {
    return () => {
      onAddOrUpdateTheme(theme);
    };
  };

  const selectTheme = (theme) => {
    return () => {
      console.log(theme);
    };
  };

  const textStyle = { ...GridElementHeaderProps(currentTheme) };

  return (
    <ThemeListWrapper border={currentTheme.border.colors.primary}>
      <FontAwesomeIcon
        icon={faPlusCircle}
        color={currentTheme.icon.colors.other.c}
        onClick={onAddOrUpdateTheme}
      />
      {themeOptions.map((themeOption) => {
        return (
          <ThemeOption
            theme={themeOption}
            textStyle={textStyle}
            onSelectTheme={selectTheme(themeOption)}
            onEditTheme={editTheme(themeOption)}
          />
        );
      })}
    </ThemeListWrapper>
  );
};

export const ManageThemes = ({ theme: currentTheme, setTheme }) => {
  const [selectedThemeFormValues, setSelectedThemeFormValues] = useState(null);
  const { themeOptions, onUpdateTheme } = useThemeOptions();

  const selectThemeForm = (formFields) => {
    setSelectedThemeFormValues(formFields);
  };

  const saveTheme = (theme) => {
    onUpdateTheme(theme);
  }

  const applyTheme = (theme) => {
    const builtTheme = buildTheme(theme);
    setTheme(builtTheme);
  }

  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(currentTheme)}>
        Manage Themes
      </SimpleText>
      {selectedThemeFormValues ? (
        <ThemeForm currentTheme={currentTheme} initialValues={selectedThemeFormValues} onSave={saveTheme} onApply={applyTheme} />
      ) : (
        <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
          <GridElement
            position={{
              startCol: 1,
              endCol: 17,
              startRow: 1,
              endRow: 10,
            }}
          >
            <ThemeListView
              themeOptions={themeOptions}
              currentTheme={currentTheme}
              onAddOrUpdateTheme={selectThemeForm}
            />
          </GridElement>
        </Grid>
      )}
    </React.Fragment>
  );
};
