import { GridElement } from "../CoreUI/Grid/GridElement";
import { Grid } from "../CoreUI/Grid/Grid";
import {
  TabSectionHeaderProps,
  GridElementHeaderProps,
} from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { useState, useRef } from "react";
import { CompactPicker } from "react-color";
import { RelativePop } from "../CoreUI/Popover/RelativePop";

const ThemeListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border-top: ${(props) => `1px solid ${props.border}`};
`;

const ThemeListView = ({ current }) => {
  return (
    <ThemeListWrapper border={current.border.colors.primary}>
      <FontAwesomeIcon
        icon={faPlusCircle}
        color={current.icon.colors.other.c}
      />
      <SimpleText {...GridElementHeaderProps(theme)}>Default</SimpleText>
      <SimpleText {...GridElementHeaderProps(theme)}>Theme 1</SimpleText>
    </ThemeListWrapper>
  );
};

const ThemeOptionWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 10px;
`;

const ColorSquare = styled.div`
  display: flex;
  align-items: center;
  background-color: ${(props) => props.color};
  height: 15px;
  width: 15px;
  &:hover {
    cursor: pointer;
  }
`;

const ThemeOption = ({
  currentTheme,
  displayName,
  value,
  optionKey,
  updateTheme,
}) => {
  const optionRef = useRef(null);
  const getOptionRef = () => {
    return optionRef;
  };

  const [pickerOpen, setPickerOpen] = useState(false);

  const onChangeComplete = (newColor) => {
    updateTheme(optionKey, newColor.hex);
    setPickerOpen(false);
  };

  const onTogglePicker = () => setPickerOpen(!pickerOpen);

  return (
    <ThemeOptionWrapper ref={optionRef}>
      <ColorSquare color={value} onClick={onTogglePicker} />
      {pickerOpen && (
        <RelativePop
          height="89px"
          width="245px"
          position={{
            horizontal: 7.5,
            vertical: 0,
          }}
          margin="20px 0 0 0"
          arrowPosition={3}
          backgroundColor={currentTheme.surface.colors.primary}
          borderColor="black"
          onClickAway={onTogglePicker}
          getParentRef={getOptionRef}
        >
          <CompactPicker color={value} onChangeComplete={onChangeComplete} />
        </RelativePop>
      )}
      <SimpleText
        color={currentTheme.text.colors.primary}
        style={currentTheme.text.styles.primary}
        size={currentTheme.text.sizes.large}
      >
        {displayName}
      </SimpleText>
    </ThemeOptionWrapper>
  );
};

const ThemeFormWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 20px;
`;

const ThemeFormView = ({ currentTheme, initialThemeOptions, formType }) => {
  const [themeOptions, setThemeOptions] = useState(initialThemeOptions);

  const updateTheme = (optionKey, newVal) => {
    setThemeOptions({
      ...themeOptions,
      [optionKey]: { ...themeOptions[optionKey], value: newVal },
    });
  };

  return (
    <ThemeFormWrapper>
      <ThemeOption
        currentTheme={currentTheme}
        displayName={themeOptions.color.displayName}
        value={themeOptions.color.value}
        optionKey={"color"}
        updateTheme={updateTheme}
      ></ThemeOption>
    </ThemeFormWrapper>
  );
};

export const ManageThemes = ({ theme: currentTheme, setTheme }) => {
  const [formOptions, setFormOptions] = useState(null);

  const onSetFormOptions = (options) => {
    setFormOptions(options);
  };

  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(theme)}>Manage Themes</SimpleText>
      <Grid numCols={16} numRows={9} rowUnit="28px" colUnit="28px" key={0}>
        <GridElement
          position={{
            startCol: 1,
            endCol: 17,
            startRow: 1,
            endRow: 10,
          }}
        >
          {formOptions ? (
            <ThemeFormView
              currentTheme={theme}
              initialThemeOptions={{
                color: { value: "red", displayName: "Color" },
              }}
            />
          ) : (
            <ThemeListView
              currentTheme={currentTheme}
              onAddOrUpdateTheme={onSetFormOptions}
            />
          )}
        </GridElement>
      </Grid>
    </React.Fragment>
  );
};
