import { Grid } from "../CoreUI/Grid/Grid";
import { GridElement } from "../CoreUI/Grid/GridElement";
import { GridElementHeaderProps } from "../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../CoreUI/Text/SimpleText";
import { DecoratedText } from "../CoreUI/Text/DecoratedText";

const ThemeFieldWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 10px;
`;

const ColorSquare = styled.div`
  background-color: ${(props) => props.color};
  height: 15px;
  width: 15px;
  &:hover {
    cursor: pointer;
  }
`;

const ThemeField = ({
    textConfig,
    displayName,
    value,
    fieldKey,
    updateTheme,
  }) => {
    const fieldRef = useRef(null);
    const getFieldRef = () => {
      return fieldRef;
    };

    const [pickerOpen, setPickerOpen] = useState(false);

    const onChangeComplete = (newColor) => {
      updateTheme(fieldKey, newColor.hex);
      setPickerOpen(false);
    };

    const onTogglePicker = () => setPickerOpen(!pickerOpen);

    return (
      <ThemeFieldWrapper ref={fieldRef}>
        {pickerOpen && (
          <RelativePop
            height="89px"
            width="245px"
            position={{
              horizontal: 7.5,
              vertical: 0,
            }}
            margin="20px 0 0 0"
            arrowPosition={3}
            backgroundColor={currentTheme.surface.colors.primary}
            borderColor="black"
            onClickAway={onTogglePicker}
            getParentRef={getFieldRef}
          >
            <CompactPicker color={value} onChangeComplete={onChangeComplete} />
          </RelativePop>
        )}
        <DecoratedText
          {...textConfig}
          decoration={<ColorSquare color={value} onClick={onTogglePicker} />}
        >
          {displayName}
        </DecoratedText>
      </ThemeFieldWrapper>
    );
  };


export const ThemeForm = ({ currentTheme, initialValues }) => {
    const [themeValues, setThemeValues] = useState(initialValues);

    const updateTheme = (fieldKey, newVal) => {
      setThemeValues({
        ...themeValues,
        [fieldKey]: newVal,
      });
    };

    const {
        text: {
          styles: { primary: primaryStyle },
          colors: {
            primary: primaryColor,
          },
          sizes: { small },
        },
      } = currentTheme;

      const fieldTextConfig = {
        style: primaryStyle,
        size: small,
        color: primaryColor,
      };

  const fields = Object.keys(initialValues).reduce((result, fieldKey) => {
    const fieldVal = initialValues[fieldKey];

    const fieldKeyParts = fieldKey.split("_");
    const fieldGroup = fieldKeyParts[0];
    const field = fieldKeyParts[fieldKeyParts.length - 1];

    if (!result[fieldGroup]) {
        result[fieldGroup] = {};
    }

    result[fieldGroup][field] = {
        fieldKey,
        displayName: field[0].toUpperCase() + field.slice(1),
        value: initialValues[fieldKey]
    }



    const displayName = fieldKey
      .split("_")
      .map((word) => word[0].toUpperCase() + word.slice(1))
      .join(" ");

    return (
      <ThemeField
        currentTheme={currentTheme}
        displayName={displayName}
        value={fieldVal}
        fieldKey={fieldKey}
        updateTheme={updateTheme}
      />
    );
  }, {});

  return (
    <Grid numCols={12} numRows={3} rowUnit="135px" colUnit="56px" key={1}>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 1,
          endRow: 2,
        }}
      >
        <SimpleText {...GridElementHeaderProps(currentTheme)}>Background</SimpleText>

      </GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 1,
          endRow: 2,
        }}
      ><SimpleText {...GridElementHeaderProps(currentTheme)}>Border</SimpleText></GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 1,
          endRow: 2,
        }}
      ><SimpleText {...GridElementHeaderProps(currentTheme)}>Surface</SimpleText></GridElement>
      <GridElement
        position={{
          startCol: 1,
          endCol: 5,
          startRow: 2,
          endRow: 4,
        }}
      ><SimpleText {...GridElementHeaderProps(currentTheme)}>Text</SimpleText></GridElement>
      <GridElement
        position={{
          startCol: 5,
          endCol: 9,
          startRow: 2,
          endRow: 4,
        }}
      ><SimpleText {...GridElementHeaderProps(currentTheme)}>Icon</SimpleText></GridElement>
      <GridElement
        position={{
          startCol: 9,
          endCol: 13,
          startRow: 2,
          endRow: 3,
        }}
      ><SimpleText {...GridElementHeaderProps(currentTheme)}>Tab</SimpleText></GridElement>
    </Grid>
  );
};
