import styled from "styled-components";
import { SimpleText } from "../Text/SimpleText";
import { DecoratedText } from "../Text/DecoratedText";
import { WithDecoration } from "../Auxiliary/WithDecoration";
import { ThemeContext } from "styled-components";
import { useContext } from "react";
import { centerMixin } from "../../../utils/styleUtils/generalCSSUtils";
import { useState } from "react";

const StyledAppBar = styled.div`
  height: 70px;
  width: 100%;
  display: flex;
`;

const AppBarLeft = styled.div`
  min-width: 210px;
  background-color: ${(props) => props.backgroundColor};
  ${centerMixin}
`;

const AppBarRight = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
  margin-right: 20px;
`;

export const AppBar = () => {
  const theme = useContext(ThemeContext);
  const { tab, text } = theme;

  return (
    <StyledAppBar>
      <AppBarLeft backgroundColor={tab.colors.primary}>
        <SimpleText
          color={text.colors.menuItem}
          style={text.styles.bold}
          size={text.sizes.medium}
          weight="bold"
        >
          STREAMER PORTAL
        </SimpleText>
      </AppBarLeft>
      <AppBarRight>
        <UserMenu />
      </AppBarRight>
    </StyledAppBar>
  );
};

const StyledUserMenu = styled.div`
  display: flex;
  align-items: center;
  margin-left: 20px;
  position: relative;
`;

const Arrow = styled.div`
  ${(props) =>
    `transform: rotate(${props.direction === "up" ? "-135" : "45"}deg);`}
  border: solid ${(props) => props.color};
  border-width: 0 2px 2px 0;
  display: inline-block;
  padding: 3px;
`;

const UserMenu = () => {
  const { text, icon, surface } = useContext(ThemeContext);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <StyledUserMenu>
      <WithDecoration
        decoration={{ end: <img src="/images/Avatar.png" />, endWidth: 50 }}
      >
        <DecoratedText
          decoration={{
            end: (
              <Arrow
                direction={dropdownOpen ? "up" : "down"}
                color={icon.colors.other.e}
              />
            ),
            endWidth: 16,
          }}
          color={text.colors.primary}
          style={text.styles.primary}
          size={text.sizes.small}
          margin="4px 10px"
          onClick={toggleDropdown}
        >
          User
        </DecoratedText>
      </WithDecoration>
      {dropdownOpen && (
        <Popover
          height="200px"
          width="197px"
          backgroundColor={surface.colors.primary}
          onClose={toggleDropdown}
        />
      )}
    </StyledUserMenu>
  );
};

const PopoverContainer = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
`;

const StyledPopover = styled.div`
  ${(props) => {
    return `
    height: ${props.height};
    width: ${props.width};
    background-color: ${props.backgroundColor};
    position: absolute; 
    left: 84.15%;
    top: 17%;

    z-index: 1;
    box-shadow: 0px 2px 10px #00000027;
    &:before {
        content: "";
        position: absolute;
        width: 0;
        height: 0;
        margin-left: -0.5em;
        left: 75%;
        box-sizing: border-box;

        border: .5em solid black;
        border-color: ${props.backgroundColor} ${props.backgroundColor} transparent transparent;

        transform-origin: 0 0;
        transform: rotate(-45deg);

        box-shadow: .5px -.5px 1px 0 #00000027;
    }
    `;
  }}
`;

const Popover = ({ onClose, ...rest }) => {
  const onPopoverClick = (e) => {
    e.stopPropagation();
  };

  return (
    <PopoverContainer onClick={onClose}>
      <StyledPopover onClick={onPopoverClick} {...rest}></StyledPopover>;
    </PopoverContainer>
  );
};
