import styled from "styled-components";
import PropTypes from "prop-types";
import { sanitizeNumericVals } from "../../../utils/styleUtils/generalCSSUtils";

const PopoverContainer = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
`;

const StyledPopover = styled.div`
  ${(props) => {
    return `
    height: ${props.height};
    width: ${props.width};
    background-color: ${props.backgroundColor};
    position: absolute; 
    left: ${props.horizontalPosition}px;
    top: ${props.verticalPosition}px;

    z-index: 1;
    box-shadow: 0px 2px 10px #00000027;
    &:before {
        content: "";
        position: absolute;
        width: 0;
        height: 0;
        margin-left: -0.5em;
        left: 75%;
        box-sizing: border-box;
        border: .5em solid ${props.borderColor};
        border-color: ${props.backgroundColor} ${props.backgroundColor} transparent transparent;
        transform-origin: 0 0;
        transform: rotate(-45deg);
        box-shadow: .5px -.5px 1px 0 #00000027;
    }
    `;
  }}
`;

export const Popover = ({
  children,
  onClickAway,
  refElement,
  height = 200,
  width = 200,
  position,
  backgroundColor,
  borderColor,
  ...props
}) => {
  const {
    height: sanitizedHeight,
    width: sanitizedWidth,
  } = sanitizeNumericVals({ height, width });

  const onPopoverClick = (e) => {
    e.stopPropagation();
  };

  let verticalPosition = position.vertical;
  let horizontalPosition = position.horizontal;
  if (refElement && refElement.current) {
    const l = refElement.current.getBoundingClientRect();
    verticalPosition += refElement.current.offsetHeight;
    horizontalPosition += refElement.current.offsetLeft;
  }

  return (
    <PopoverContainer onClick={onClickAway} {...props}>
      <StyledPopover
        onClick={onPopoverClick}
        height={sanitizedHeight}
        width={sanitizedWidth}
        verticalPosition={verticalPosition}
        horizontalPosition={horizontalPosition}
        backgroundColor={backgroundColor}
        borderColor={borderColor}
      >
        {children}
      </StyledPopover>
      ;
    </PopoverContainer>
  );
};

Popover.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func,
  height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  backgroundColor: PropTypes.string,
  borderColor: PropTypes.string,
  position: PropTypes.shape({
    vertical: PropTypes.number,
    horizontal: PropTypes.number,
  }),
};
