import {
  TabSectionHeaderProps
} from "../../../utils/styleUtils/commonTextProps";
import { SimpleText } from "../../CoreUI/Text/SimpleText";
import { useThemeOptions } from "../../../hooks/useThemeOptions";
import { ThemeForm } from "./theme_form";
import buildTheme from "../../../themes/buildTheme";
import { ThemeListView } from "./theme_list";
import { getThemes, updateTheme, createTheme, deleteTheme } from "../../../api/theme_api";
import { useEffect, useState } from "react";

export const ManageThemes = ({ theme: currentTheme, setTheme }) => {
  const [selectedThemeFormValues, setSelectedThemeFormValues] = useState(null);
  // const { themeOptions, onUpdateTheme, onCreateTheme, onDeleteTheme } = useThemeOptions();

  const [themeOptions, setThemeOptions] = useState();

  useEffect(() => {
      const callGetThemes = async () => {
          const options = await getThemes();  
          setThemeOptions(options);  
      }

      callGetThemes();
  }, []);

  const onUpdateTheme = async (theme) => {
      const updatedTheme = await updateTheme(theme);
      const updatedThemeOptions = themeOptions.map(theme => theme.id !== updatedTheme.id ? theme : updatedTheme);
      setThemeOptions(updatedThemeOptions);
  }

  const onCreateTheme = async (theme) => {
      const newTheme = await createTheme(theme);
      const updatedThemeOptions = themeOptions.concat([newTheme]);
      setThemeOptions(updatedThemeOptions);
  }

  const onDeleteTheme = async (themeId) => {
      await deleteTheme(themeId);
      const updatedThemeOptions = themeOptions.filter(theme => theme.id !== themeId);
      setThemeOptions(updatedThemeOptions);
  }

  const selectThemeForm = (formFields) => {
    setSelectedThemeFormValues(formFields);
  };

  const saveTheme = (theme) => {
    if (theme.id) {
      onUpdateTheme(theme);
    } else {
      onCreateTheme(theme);
    }
  };

  const applyTheme = (theme) => {
    const builtTheme = buildTheme(theme);
    setTheme(builtTheme);
  };

  const closeForm = () => {
    setSelectedThemeFormValues(null);
  };

  return (
    <React.Fragment>
      <SimpleText {...TabSectionHeaderProps(currentTheme)}>
        Manage Themes
      </SimpleText>
      {selectedThemeFormValues ? (
        <ThemeForm
          currentTheme={currentTheme}
          initialValues={selectedThemeFormValues}
          onSave={saveTheme}
          onApply={applyTheme}
          onDone={closeForm}
        />
      ) : (
        <ThemeListView
          themeOptions={themeOptions}
          currentTheme={currentTheme}
          onSelectTheme={applyTheme}
          onAddOrUpdateTheme={selectThemeForm}
          onDeleteTheme={onDeleteTheme}
        />
      )}
    </React.Fragment>
  );
};
